import React from 'react';

function EventCards() {
    const events = [
        {
            title: "QUARCC wins $10,000 at McGill-FIAM Hackathon",
            description: "Our team QUARCC won first place at the McGill-FIAM Hackathon...",
            date: "2024-11-10T23:30:00.000Z",
            image: "/path-to-image.jpg",
            link: "/events/quarcc-wins"
        },
        // Add more events here...
    ];

    return (
        <ul role="list" className="grid grid-cols-1 gap-5 sm:grid-cols-2 sm:gap-6 lg:grid-cols-3">
            {events.map((event, index) => (
                <li key={index}>
                    <a className="size-fit" href={event.link}>
                        <div className="rounded-xl border bg-card text-card-foreground">
                            <img 
                                alt={event.title}
                                loading="lazy"
                                width="500"
                                height="500"
                                decoding="async"
                                className="aspect-square size-full rounded-t-xl object-cover"
                                src={event.image}
                            />
                            <div className="flex flex-col space-y-1.5 p-6">
                                <h3 className="font-semibold tracking-tight line-clamp-2 leading-snug">
                                    {event.title}
                                </h3>
                                <p className="text-sm text-muted-foreground line-clamp-2">
                                    {event.description}
                                </p>
                            </div>
                            <div className="flex p-6 pt-0 flex-col items-start justify-between sm:flex-row sm:items-center">
                                <div className="flex items-start gap-x-2 text-sm text-muted-foreground sm:items-center">
                                    <CalendarIcon />
                                    <time dateTime={event.date} className="text-nowrap text-xs lg:text-base">
                                        {new Date(event.date).toLocaleDateString('en-US', {
                                            month: 'long',
                                            day: 'numeric',
                                            year: 'numeric'
                                        })}
                                    </time>
                                </div>
                                <a href={event.link}>
                                    <button className="inline-flex items-center justify-center whitespace-nowrap rounded-md text-sm font-medium transition-colors focus-visible:outline-none focus-visible:ring-1 focus-visible:ring-ring disabled:pointer-events-none disabled:opacity-50 disabled:cursor-not-allowed text-primary underline-offset-4 hover:underline h-9 py-2 group w-fit px-0">
                                        <span>Learn more</span>
                                        <ArrowIcon />
                                        <span className="sr-only">Learn more about {event.title}</span>
                                    </button>
                                </a>
                            </div>
                        </div>
                    </a>
                </li>
            ))}
        </ul>
    );
}

function CalendarIcon() {
    return (
        <svg width="15" height="15" viewBox="0 0 15 15" fill="none" xmlns="http://www.w3.org/2000/svg" className="size-4">
            <path d="M4.5 1C4.77614 1 5 1.22386 5 1.5V2H10V1.5C10 1.22386 10.2239 1 10.5 1C10.7761 1 11 1.22386 11 1.5V2H12.5C13.3284 2 14 2.67157 14 3.5V12.5C14 13.3284 13.3284 14 12.5 14H2.5C1.67157 14 1 13.3284 1 12.5V3.5C1 2.67157 1.67157 2 2.5 2H4V1.5C4 1.22386 4.22386 1 4.5 1ZM10 3V3.5C10 3.77614 10.2239 4 10.5 4C10.7761 4 11 3.77614 11 3.5V3H12.5C12.7761 3 13 3.22386 13 3.5V5H2V3.5C2 3.22386 2.22386 3 2.5 3H4V3.5C4 3.77614 4.22386 4 4.5 4C4.77614 4 5 3.77614 5 3.5V3H10ZM2 6V12.5C2 12.7761 2.22386 13 2.5 13H12.5C12.7761 13 13 12.7761 13 12.5V6H2Z" fill="currentColor" fillRule="evenodd" clipRule="evenodd"/>
        </svg>
    );
}

function ArrowIcon() {
    return (
        <svg width="15" height="15" viewBox="0 0 15 15" fill="none" xmlns="http://www.w3.org/2000/svg" className="ml-1.5 size-4 transition-transform group-hover:translate-x-0.5">
            <path d="M8.14645 3.14645C8.34171 2.95118 8.65829 2.95118 8.85355 3.14645L12.8536 7.14645C13.0488 7.34171 13.0488 7.65829 12.8536 7.85355L8.85355 11.8536C8.65829 12.0488 8.34171 12.0488 8.14645 11.8536C7.95118 11.6583 7.95118 11.3417 8.14645 11.1464L11.2929 8H2.5C2.22386 8 2 7.77614 2 7.5C2 7.22386 2.22386 7 2.5 7H11.2929L8.14645 3.85355C7.95118 3.65829 7.95118 3.34171 8.14645 3.14645Z" fill="currentColor" fillRule="evenodd" clipRule="evenodd"/>
        </svg>
    );
}

export default EventCards;
