import React, { useEffect, useState } from 'react';

/* the bar on top lol */
function Navigation(prop) {
    const [scrollingUp, setScrollingUp] = useState(true);
    const [lastScrollY, setLastScrollY] = useState(0);

    const handleScroll = () => {
        const currentScrollY = window.scrollY;
        setScrollingUp(currentScrollY < lastScrollY);
        setLastScrollY(currentScrollY);
    };

    useEffect(() => {
        window.addEventListener('scroll', handleScroll);
        return () => {
            window.removeEventListener('scroll', handleScroll);
        };
    }, [lastScrollY]);

    return (
        <div className={`navbar ${scrollingUp ? 'visible' : 'hidden'}`}>
            <div className="hidden top-2/4 absolute left-2/4 sm:inline-flex [transform:matrix(1,0,0,1,-181.125,-18)]">
                <nav {...{"dir":"ltr"}} className="z-10 flex flex-1 relative max-w-max items-center justify-center">
                    <div className="relative">
                        <ul {...{"dir":"ltr"}} className="h-9 m-0 p-1 flex-1 list-none inline-flex text-[oklch(55.553%_0_none)] items-center justify-center [list-style:none] [background-color:oklch(100%_0_none)]" style={{...{"borderRadius":"9999px"}}}>
                            <li>
                                <a className="px-3 py-1 text-sm leading-5 text-[oklch(96.833%_0.01405_-72.601)] inline-flex font-medium ease-in-out items-center justify-center transition-all duration-[0.15s] whitespace-nowrap [background-color:oklch(44.889%_0.15545_-73.341)] [text-decoration:none_solid_oklch(96.833%_0.01405_-72.601)]" style={{...{"borderRadius":"9999px"}}}>
                                    Home
                                </a>
                            </li>
                            <li className="mr-0 ml-[4px]">
                                <a className="px-3 py-1 text-sm leading-5 text-[oklch(55.553%_0_none)] inline-flex font-medium ease-in-out items-center justify-center transition-all duration-[0.15s] whitespace-nowrap [text-decoration:none_solid_oklch(55.553%_0_none)]" style={{...{"borderRadius":"9999px"}}}>
                                    About
                                </a>
                            </li>
                            <li className="mr-0 ml-[4px]">
                                <a className="px-3 py-1 text-sm leading-5 text-[oklch(55.553%_0_none)] inline-flex font-medium ease-in-out items-center justify-center transition-all duration-[0.15s] whitespace-nowrap [text-decoration:none_solid_oklch(55.553%_0_none)]" style={{...{"borderRadius":"9999px"}}}>
                                    Activities
                                </a>
                            </li>
                            <li className="mr-0 ml-[4px]">
                                <a className="px-3 py-1 text-sm leading-5 text-[oklch(55.553%_0_none)] inline-flex font-medium ease-in-out items-center justify-center transition-all duration-[0.15s] whitespace-nowrap [text-decoration:none_solid_oklch(55.553%_0_none)]" style={{...{"borderRadius":"9999px"}}}>
