import React, { useState } from 'react';

function ConuAISection() {
    const [query, setQuery] = useState('');
    const [response, setResponse] = useState('');
    const [isLoading, setIsLoading] = useState(false);

    const handleSubmit = async (e) => {
        e.preventDefault();
        setIsLoading(true);

        try {
            const res = await fetch('http://localhost:5000/api/query', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ query }),
            });

            const data = await res.json();
            if (data.error) {
                throw new Error(data.error);
            }
            setResponse(data.response);
        } catch (error) {
            console.error('Error:', error);
            setResponse('Sorry, there was an error processing your request.');
        } finally {
            setIsLoading(false);
        }
    };

    return (
        <div className="container mx-auto px-4 py-8">
            <div className="max-w-3xl mx-auto">
                <h1 className="text-3xl font-bold mb-8">ConuAI Assistant</h1>
                
                <form onSubmit={handleSubmit} className="mb-6">
                    <div className="relative">
                        <input 
                            type="text"
                            value={query}
                            onChange={(e) => setQuery(e.target.value)}
                            className="w-full p-4 pr-16 rounded-full border border-gray-300 focus:outline-none focus:ring-2 focus:ring-purple-500"
                            placeholder="How can ConuAI help you today?"
                        />
                        <button 
                            type="submit"
                            disabled={isLoading}
                            className="absolute right-2 top-1/2 transform -translate-y-1/2 px-4 py-2 bg-[oklch(44.889%_0.15545_-73.341)] text-white rounded-full hover:bg-opacity-90 disabled:opacity-50"
                        >
                            {isLoading ? 'Loading...' : 'Ask'}
                        </button>
                    </div>
                </form>

                {response && (
                    <div className="bg-white p-6 rounded-lg shadow-md">
                        <p className="whitespace-pre-wrap">{response}</p>
                    </div>
                )}
            </div>
        </div>
    );
}

export default ConuAISection;
