import React, { useState } from 'react';
import Navigation from './Navigation';
import ActivitiesSection from './ActivitiesSection';
import ActivityCard from './ActivityCard';
// Import other sections as needed

function App() {
    const [activeSection, setActiveSection] = useState('home'); // Default section

    const handleNavigationClick = (section) => {
        setActiveSection(section);
    };

    return (
        <div>
            <Navigation onNavClick={handleNavigationClick} />
            {activeSection === 'activities' && <ActivitiesSection />}
            {/* Add other sections here, conditionally rendering based on activeSection */}
        </div>
    );
}

export default App;
