import React, { useState } from 'react';
/the bar on top lol/
function Navigation({ setCurrentSection, currentSection }) {
    return (
        <header className="sticky top-0 z-50 w-full border-b bg-background/95 backdrop-blur supports-[backdrop-filter]:bg-background/60">
            <nav className={`${isOpen ? '' : 'hidden'} items-center justify-between lg:flex`}>
                <ul className="flex flex-col gap-6 text-base lg:flex-row lg:gap-8">
                    <li>
                        <a onClick={() => setCurrentSection('home')} className={currentSection === 'home' ? 'active' : ''}>
                            Home
                        </a>
                    </li>
                    {/* ... other nav items ... */}
                </ul>
            </nav>
        </header>
    );
}
export default Navigation;
