import React, { useState } from 'react';
import { Link } from 'react-router-dom';

function Navigation(prop) {
    const [activeLink, setActiveLink] = useState('Home');

    const handleLinkClick = (link) => {
        setActiveLink(link);
    };

    return (
        <div className="hidden top-2/4 absolute left-2/4 sm:inline-flex [transform:matrix(1,0,0,1,-181.125,-18)] navbar">
            <nav {...{"dir":"ltr"}} className="z-10 flex flex-1 relative max-w-max items-center justify-center">
                <div className="relative">
                    <ul {...{"dir":"ltr"}} className="h-9 m-0 p-1 flex-1 list-none inline-flex text-[oklch(55.553%_0_none)] items-center justify-center [list-style:none] [background-color:oklch(100%_0_none)]" style={{ borderRadius: "9999px" }}>
                        {['Home', 'About', 'Activities', 'Team', 'ConuAI'].map((link) => (
                            <li key={link} className="mr-0 ml-[4px]">
                                <Link
                                    to={link === 'Home' ? '/' : `/${link.toLowerCase()}`}
                                    onClick={() => handleLinkClick(link)}
                                    className={`px-3 py-1 text-sm leading-5 inline-flex font-medium ease-in-out items-center justify-center transition-all duration-[0.15s] whitespace-nowrap ${activeLink === link ? 'active' : ''}`}
                                    style={{ borderRadius: "9999px", textDecoration: 'none' }}
                                >
                                    {link}
                                </Link>
                            </li>
                        ))}
                    </ul>
                </div>
                <div className="flex left-0 absolute top-full justify-center"></div>
            </nav>
        </div>
    );
}

export default Navigation;
