import React, { useState } from 'react';

function Navigation({ onNavClick }) {
    const [activeIndex, setActiveIndex] = useState(0); // Default to the first item

    const handleClick = (index) => {
        setActiveIndex(index);
        onNavClick(index); // Call the passed function to update the active index in HeroSection
    };

    return (
        <div className="hidden top-2/4 absolute left-2/4 sm:inline-flex [transform:matrix(1,0,0,1,-181.125,-18)]">
            <nav {...{"dir":"ltr"}} className="z-10 flex flex-1 relative max-w-max items-center justify-center">
                <div className="relative">
                    <ul {...{"dir":"ltr"}} className="h-9 m-0 p-1 flex-1 list-none inline-flex text-[oklch(55.553%_0_none)] items-center justify-center [list-style:none] [background-color:oklch(100%_0_none)]" style={{...{"borderRadius":"9999px"}}}>
                        {['Home', 'About', 'Activities', 'Team', 'ConuAI'].map((item, index) => (
                            <li key={item} className="mr-0 ml-[4px]">
                                <a
                                    className={`nav-link px-3 py-1 text-sm leading-5 inline-flex font-medium ease-in-out items-center justify-center transition-all duration-[0.15s] whitespace-nowrap ${activeIndex === index ? 'active' : ''}`}
                                    onClick={() => handleClick(index)}
                                    style={{...{"borderRadius":"9999px"}}}
                                >
                                    {item}
                                </a>
                            </li>
                        ))}
                    </ul>
                </div>
                <div className="flex left-0 absolute top-full justify-center"></div>
            </nav>
        </div>
    );
}
export default Navigation;
