import React from 'react';
import { Link, useMatch, useResolvedPath } from "react-router-dom";

function CustomLink({ to, children, ...props }) {
    const resolvedPath = useResolvedPath(to);
    const isActive = useMatch({ path: resolvedPath.pathname, end: true });

    return (
        <li className={`${props.className || ''}`}>
            <Link 
                to={to} 
                className={`nav-link px-3 py-1 text-sm leading-5 inline-flex font-medium ease-in-out items-center justify-center transition-all duration-[0.15s] whitespace-nowrap ${isActive ? 'active' : ''}`}
                style={{ borderRadius: "9999px" }}
            >
                {children}
            </Link>
        </li>
    );
}

function Navigation() {
    return (
        <div className="hidden top-2/4 absolute left-2/4 sm:inline-flex [transform:matrix(1,0,0,1,-181.125,-18)]">
            <nav dir="ltr" className="z-10 flex flex-1 relative max-w-max items-center justify-center">
                <div className="relative">
                    <ul dir="ltr" className="h-9 m-0 p-1 flex-1 list-none inline-flex text-[oklch(55.553%_0_none)] items-center justify-center [list-style:none] [background-color:oklch(100%_0_none)]" style={{ borderRadius: "9999px" }}>
                        <CustomLink to="/">Home</CustomLink>
                        <CustomLink to="/about" className="mr-0 ml-[4px]">About</CustomLink>
                        <CustomLink to="/team" className="mr-0 ml-[4px]">Team</CustomLink>
                        <CustomLink to="/activities" className="mr-0 ml-[4px]">Activities</CustomLink>
                        <CustomLink to="/conuai" className="mr-0 ml-[4px]">ConuAI</CustomLink>
                    </ul>
                </div>
                <div className="flex left-0 absolute top-full justify-center"></div>
            </nav>
        </div>
    );
}

export default Navigation;
