import React from 'react';
import { Link, useLocation } from "react-router-dom";

function Navigation() {
    const location = useLocation();

    return (
        <nav className="flex justify-center space-x-4 p-4">
            <Link 
                to="/"
                className={`px-4 py-2 rounded ${location.pathname === '/' ? 'bg-blue-500 text-white' : 'bg-gray-200'}`}
            >
                Home
            </Link>
            <Link 
                to="/about"
                className={`px-4 py-2 rounded ${location.pathname === '/about' ? 'bg-blue-500 text-white' : 'bg-gray-200'}`}
            >
                About
            </Link>
            <Link 
                to="/team"
                className={`px-4 py-2 rounded ${location.pathname === '/team' ? 'bg-blue-500 text-white' : 'bg-gray-200'}`}
            >
                Team
            </Link>
            <Link 
                to="/activities"
                className={`px-4 py-2 rounded ${location.pathname === '/activities' ? 'bg-blue-500 text-white' : 'bg-gray-200'}`}
            >
                Activities
            </Link>
            <Link 
                to="/conuai"
                className={`px-4 py-2 rounded ${location.pathname === '/conuai' ? 'bg-blue-500 text-white' : 'bg-gray-200'}`}
            >
                ConuAI
            </Link>
            <Link 
                to="/quantfund"
                className={`px-4 py-2 rounded ${location.pathname === '/quantfund' ? 'bg-blue-500 text-white' : 'bg-gray-200'}`}
            >
                The Fund
            </Link>
        </nav>
    );
}

export default Navigation;
