import React, { useState } from 'react';

function HeaderActions(prop) {
    const [activeSection, setActiveSection] = useState('home'); // Default to 'home'

    const handleSectionChange = (section) => {
        setActiveSection(section);
    };

    return (
        <div className="flex gap-2 items-center">
            <button onClick={() => handleSectionChange('home')}>Home</button>
            <button onClick={() => handleSectionChange('activities')}>Activities</button>
            <button onClick={() => handleSectionChange('events')}>Events</button>

            {/* Conditional rendering based on activeSection */}
            {activeSection === 'home' && (
                <div>
                    {/* Render all components for Home */}
                    <p>All Home Components</p>
                </div>
            )}
            {activeSection === 'activities' && (
                <div>
                    {/* Render only components for Activities */}
                    <p>Activities Components</p>
                </div>
            )}
            {activeSection === 'events' && (
                <div>
                    {/* Render only components for Events */}
                    <p>Events Components</p>
                </div>
            )}
        </div>
    );
}

export default HeaderActions;
