import React, { useState } from 'react';
/*the bar on top lol*/ 
function Navigation({ onNavClick }) {
    const [activeIndex, setActiveIndex] = useState(0); // Default to the first item

    const handleClick = (index, section) => {
        setActiveIndex(index);
        onNavClick(section); // Call the function passed from the parent
    };

    return (
<div className="hidden top-2/4 absolute left-2/4 sm:inline-flex [transform:matrix(1,0,0,1,-181.125,-18)]"
><nav {...{"dir":"ltr"}} className="z-10 flex flex-1 relative max-w-max items-center justify-center"
  ><div className="relative"
    ><ul {...{"dir":"ltr"}} className="h-9 m-0 p-1 flex-1 list-none inline-flex text-[oklch(55.553%_0_none)] items-center justify-center [list-style:none] [background-color:oklch(100%_0_none)]" style={{...{"borderRadius":"9999px"}}}
      ><li
        ><a className={`nav-link ${activeIndex === 0 ? 'active' : ''}`} onClick={() => handleClick(0, 'home')}>Home</a
          ></li
        ><li className="mr-0 ml-[4px]"
        ><a className={`nav-link ${activeIndex === 1 ? 'active' : ''}`} onClick={() => handleClick(1, 'about')}>About</a
          ></li
        ><li className="mr-0 ml-[4px]"
        ><a className={`nav-link ${activeIndex === 2 ? 'active' : ''}`} onClick={() => handleClick(2, 'activities')}>Activities</a
          ></li
        ><li className="mr-0 ml-[4px]"
        ><a className={`nav-link ${activeIndex === 3 ? 'active' : ''}`} onClick={() => handleClick(3, 'team')}>Team</a
          ></li
        ><li className="mr-0 ml-[4px]"
        ><a className={`nav-link ${activeIndex === 4 ? 'active' : ''}`} onClick={() => handleClick(4, 'conuai')}>ConuAI</a
          ></li
        ></ul
      ></div
    ><div className="flex left-0 absolute top-full justify-center"
    ></div
    ></nav
  ></div
>
);
}
export default Navigation;
