import React, { useState } from 'react';
import Navigation from './Navigation.jsx';
import HeroSection from './HeroSection.jsx';
import ActivitiesSection from './ActivitiesSection.jsx';

function App() {
    const [activeIndex, setActiveIndex] = useState(0); // Default to the first item

    const handleNavigationClick = (index) => {
        setActiveIndex(index);
    };

    return (
        <div>
            <Navigation onNavClick={handleNavigationClick} />
            {activeIndex === 4 && <HeroSection />} {/* Show HeroSection only if "ConuAI" is active */}
            <ActivitiesSection />
            {/* Other components can be added here */}
        </div>
    );
}

export default App;
