import React, { useState } from 'react';
/the bar on top lol/
function Navigation({ setCurrentSection, currentSection }) {
    return (
        <nav className={`${isOpen ? '' : 'hidden'} lg:block`} aria-label="Global">
            <ul className="flex flex-col gap-6 lg:flex-row lg:gap-4">
                {[
                    ['Home', 'home'],
                    ['About', 'about'],
                    ['Team', 'team'],
                    ['Activities', 'events'],
                    ['ConuAI', 'conuai'],
                    ['Quant Fund', 'quantfund']
                ].map(([label, value]) => (
                    <li key={value}>
                        <button
                            onClick={() => setCurrentSection(value)}
                            className={`${currentSection === value ? 'text-gray-900' : 'text-gray-500'} relative rounded-lg px-2 py-1 text-sm transition-colors delay-150 hover:text-gray-900`}
                        >
                            {label}
                        </button>
                    </li>
                ))}
            </ul>
        </nav>
    );
}
export default Navigation;
