import React from 'react';

function Navigation({ activeSection, onNavigationChange }) {
  const navItems = [
    { id: 'home', label: 'Home' },
    { id: 'about', label: 'About' },
    { id: 'activities', label: 'Activities' },
    { id: 'team', label: 'Team' },
    { id: 'conuai', label: 'ConuAI' }
  ];

  const handleClick = (sectionId) => {
    onNavigationChange(sectionId);
  };

  return (
    <div className="hidden sm:inline-flex absolute left-1/2 top-1/2 -translate-x-1/2 -translate-y-1/2">
      <nav className="z-10 flex flex-1 relative max-w-max items-center justify-center">
        <div className="relative">
          <ul className="h-9 m-0 p-1 flex-1 list-none inline-flex items-center justify-center bg-white rounded-full">
            {navItems.map((item, index) => (
              <li key={item.id} className={index > 0 ? "ml-1" : ""}>
                <button
                  className={`
                    px-3 py-1 text-sm leading-5 inline-flex font-medium items-center justify-center
                    rounded-full transition-all duration-150 ease-in-out
                    ${activeSection === item.id 
                      ? 'bg-gray-100 text-gray-900' 
                      : 'text-gray-600 hover:text-gray-900'
                    }
                  `}
                  onClick={() => handleClick(item.id)}
                >
                  {item.label}
                </button>
              </li>
            ))}
          </ul>
        </div>
      </nav>
    </div>
  );
}

export default Navigation;
