import React, { useState } from 'react';
import HeroContent from './HeroContent.jsx';
import ActivitiesSection from './ActivitiesSection.jsx';
import SponsorsSection from './SponsorsSection.jsx';
import RecentEventsSection from './RecentEventsSection.jsx';
import Navigation from './Navigation.jsx';

function HeroSection() {
  const [activeSection, setActiveSection] = useState('home');

  // Callback function to pass to Navigation
  const handleNavigationChange = (section) => {
    setActiveSection(section);
  };

  // Render the appropriate section based on activeSection
  const renderActiveSection = () => {
    switch (activeSection) {
      case 'home':
        return <HeroContent />;
      case 'activities':
        return <ActivitiesSection />;
      case 'sponsors':
        return <SponsorsSection />;
      case 'events':
        return <RecentEventsSection />;
      default:
        return <HeroContent />;
    }
  };

  return (
    <div className="relative w-full mx-auto max-w-7xl">
      <Navigation activeSection={activeSection} onNavigationChange={handleNavigationChange} />
      <div className="px-6">
        {renderActiveSection()}
      </div>
    </div>
  );
}

export default HeroSection;
