import React, { useState } from 'react';
import Navigation from './Navigation';
import ActivitiesSection from './ActivitiesSection'; // Import your ActivitiesSection

function App() {
    const [selectedIndex, setSelectedIndex] = useState(0); // Default to Home

    const handleSelect = (index) => {
        setSelectedIndex(index);
    };

    return (
        <div>
            <Navigation onSelect={handleSelect} />
            {selectedIndex === 2 ? ( // Only show ActivitiesSection when Activities is selected
                <ActivitiesSection />
            ) : (
                // Render other sections based on selectedIndex
                <div>
                    {/* Render other components based on selectedIndex */}
                    {selectedIndex === 0 && <HomeSection />}
                    {selectedIndex === 1 && <AboutSection />}
                    {selectedIndex === 3 && <TeamSection />}
                    {selectedIndex === 4 && <ConuAISection />}
                </div>
            )}
        </div>
    );
}

export default App;
