import React, { useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';

function Navigation() {
    const navigate = useNavigate();
    const location = useLocation();
    const [activeIndex, setActiveIndex] = useState(0);

    // Helper function to get the index based on pathname
    const getIndexFromPath = (pathname) => {
        switch(pathname) {
            case '/': return 0;
            case '/about': return 1;
            case '/activities': return 2;
            case '/team': return 3;
            case '/conuai': return 4;
            default: return 0;
        }
    };

    // Set initial active index based on current path
    React.useEffect(() => {
        setActiveIndex(getIndexFromPath(location.pathname));
    }, [location.pathname]);

    const handleClick = (index) => {
        setActiveIndex(index);
        // Navigate to corresponding route
        switch(index) {
            case 0:
                navigate('/');
                break;
            case 1:
                navigate('/about');
                break;
            case 2:
                navigate('/activities');
                break;
            case 3:
                navigate('/team');
                break;
            case 4:
                navigate('/conuai');
                break;
            default:
                navigate('/');
        }
    };

    return (
        <div className="hidden top-2/4 absolute left-2/4 sm:inline-flex [transform:matrix(1,0,0,1,-181.125,-18)]">
            <nav {...{"dir":"ltr"}} className="z-10 flex flex-1 relative max-w-max items-center justify-center">
                <div className="relative">
                    <ul {...{"dir":"ltr"}} className="h-9 m-0 p-1 flex-1 list-none inline-flex text-[oklch(55.553%_0_none)] items-center justify-center [list-style:none] [background-color:oklch(100%_0_none)]" style={{...{"borderRadius":"9999px"}}}>
                        <li>
                            <a className={`nav-link px-3 py-1 text-sm leading-5 inline-flex font-medium ease-in-out items-center justify-center transition-all duration-[0.15s] whitespace-nowrap ${activeIndex === 0 ? 'active' : ''}`} onClick={() => handleClick(0)} style={{...{"borderRadius":"9999px"}}}>
                                Home
                            </a>
                        </li>
                        <li className="mr-0 ml-[4px]">
                            <a className={`nav-link px-3 py-1 text-sm leading-5 inline-flex font-medium ease-in-out items-center justify-center transition-all duration-[0.15s] whitespace-nowrap ${activeIndex === 1 ? 'active' : ''}`} onClick={() => handleClick(1)} style={{...{"borderRadius":"9999px"}}}>
                                About
                            </a>
                        </li>
                        <li className="mr-0 ml-[4px]">
                            <a className={`nav-link px-3 py-1 text-sm leading-5 inline-flex font-medium ease-in-out items-center justify-center transition-all duration-[0.15s] whitespace-nowrap ${activeIndex === 2 ? 'active' : ''}`} onClick={() => handleClick(2)} style={{...{"borderRadius":"9999px"}}}>
                                Activities
                            </a>
                        </li>
                        <li className="mr-0 ml-[4px]">
                            <a className={`nav-link px-3 py-1 text-sm leading-5 inline-flex font-medium ease-in-out items-center justify-center transition-all duration-[0.15s] whitespace-nowrap ${activeIndex === 3 ? 'active' : ''}`} onClick={() => handleClick(3)} style={{...{"borderRadius":"9999px"}}}>
                                Team
                            </a>
                        </li>
                        <li className="mr-0 ml-[4px]">
                            <a className={`nav-link px-3 py-1 text-sm leading-5 inline-flex font-medium ease-in-out items-center justify-center transition-all duration-[0.15s] whitespace-nowrap ${activeIndex === 4 ? 'active' : ''}`} onClick={() => handleClick(4)} style={{...{"borderRadius":"9999px"}}}>
                                ConuAI
                            </a>
                        </li>
                    </ul>
                </div>
                <div className="flex left-0 absolute top-full justify-center">
                </div>
            </nav>
        </div>
    );
}

export default Navigation;
